import React, { useContext, useEffect } from 'react'
import { useParams } from 'react-router-dom'
import { StocksContext } from '../context/StocksContext';

const StockDetails = () => {

  const {id} = useParams();
  const { selectedStock, setSelectedStock } = useContext(StocksContext);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const url = `http://localhost:3000/api/v1/stocks/${id}`;
        const options = {
          method: "GET",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
        };
        await fetch(url, options)
          .then((response) => {
            if (!response.ok) {
              throw new Error("Error: " + response.status);
            }
            return response.json();
          })
          .then((responseJson) => {
            console.log(responseJson.holdings.stock);
            setSelectedStock(responseJson.holdings.stock);
          });
      } catch (err) {
        console.log(err);
      }
    };
    fetchData();
    // Empty dependency array called here, which makes it run only once on MOUNT //
  }, []);
  
  return (
    <div>
      {selectedStock.ticker_symbol}
    </div>
  )
}

export default StockDetails
