  };

  updateROI = () => {
    let initial = this.state.purchasePrice;
    let current = this.state.currentPrice;
    let ROI = (((current - initial) / initial) * 100).toFixed(2) + "%";
    // let valueROI = "$" + ((quantity * current) - (quantity * initial)).toFixed(2);
    //   console.log(valueROI);
    this.setState({
      ROI: ROI
    });
  };

  render() {
    return (
        <div className="formContainer">
          <Switch>
            <Route exact path = "/" component = {LandingPage} />
            <Route 
              exact path = "/home" 
              render={() => {
                return (
                  <Home
                    ROI={this.state.ROI}
                    currentMarketPrice={this.state.currentMarketPrice}
                  />
                );
              }}
              />
            <Route 
              exact path = "/stocks/:id" 
              render={() => {
                return (
                  <StockDetails
                    getStockPrice={this.getStockPrice}
                    />
                )
              }} />
            <Route exact path = "/stocks/:id/update" component = {UpdatePost} />
            <Route exact path = "/login" component = {Login} />
            <Route exact path = "/signup" component = {SignUp} />
          </Switch>
        </div>
    )
  }
};

export default App;
