import React from "react";
import { Switch, Route } from "react-router-dom";
import Home from '../routes/Home';
import LandingPage from '../routes/LandingPage/LandingPage';
import Login from '../routes/Login';
import SignUp from '../routes/SignUp';
import StockDetails from '../routes/StockDetails';
import UpdatePost from '../routes/UpdatePost/UpdatePost';
import "./App.css";

class App extends React.Component {

  state = {
    ROI: 0,
    currentMarketPrice: 121.08,
    purchasePrice: 100.22
  }

  getStockPrice = (e) => {
    e.preventDefault();
    fetch(
      `https://twelve-data1.p.rapidapi.com/price?symbol=${this.state.tickerSymbol}&outputsize=30&format=json`,
      {
        method: "GET",
        headers: {
          "x-rapidapi-key":
            "f31f4c4ae7msh19ad0c55e44bda2p1efe8ajsn5d61ef1eec85",
          "x-rapidapi-host": "twelve-data1.p.rapidapi.com",
        },
      }
    )
      .then((response) => {
        if (response.ok) {
          return response.json();
        }
        throw new Error(response.statusText);
      })
      .then((responseJson) => {
        this.setState({
          currentMarketPrice: responseJson.price,
        })
      })
      .then(() => {
        this.updateROI();
      })
      .catch((err) => {
        console.log(err);
      });
  };

  updateROI = () => {
    let initial = this.state.purchasePrice;
    let current = this.state.currentPrice;
    let ROI = (((current - initial) / initial) * 100).toFixed(2) + "%";
    // let valueROI = "$" + ((quantity * current) - (quantity * initial)).toFixed(2);
    //   console.log(valueROI);
    this.setState({
      ROI: ROI
    });
  };

  render() {
    return (
        <div className="formContainer">
          <Switch>
            <Route exact path = "/" component = {LandingPage} />
            <Route 
              exact path = "/home" 
              render={() => {
                return (
                  <Home
                    ROI={this.state.ROI}
                    currentMarketPrice={this.state.currentMarketPrice}
                    getStockPrice={this.getStockPrice}

                  />
                );
              }}
              />
            <Route exact path = "/stocks/:id" component = {StockDetails} />
            <Route exact path = "/stocks/:id/update" component = {UpdatePost} />
            <Route exact path = "/login" component = {Login} />
            <Route exact path = "/signup" component = {SignUp} />
          </Switch>
        </div>
    )
  }
};

export default App;
