import React, { useEffect, useContext } from 'react';
import { StocksContext } from '../../context/StocksContext';
import './StockHoldings.css';

const UpdateContext = (responseJson) => {
  const { stocks, setStocks } = useContext(StocksContext);
  const updatedContext = () => {
    setStocks(responseJson.holdings.stocks)
  };
  updatedContext(responseJson);
}

function StockHoldings(props) {
  useEffect(() => {
    const fetchData = async () => {
      try {
        const url = "http://localhost:3000/api/v1/stocks";
        const options = {
          method: 'GET',
          headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json;charset=UTF-8'
          }
        }
        const response = await fetch (url, options)
          .then(response => {
            if (!response.ok) {
              throw new Error("Error: " + response.status);
            }
            return response.json();
          })
          .then(responseJson => {
            UpdateContext(responseJson);
          })
          // console.log(response.holdings.stocks);
          // setStocks(response.holdings.stocks);
      } catch (err) {
        console.log(err);
      }
    }
        fetchData();
}, []);

  return (
    <div className="stockHoldings">
      <table className="holdingsTable">
        <thead>
          <tr className="tableRow">
            <th scope="column1">Ticker Symbol</th>
            <th scope="column2">Status</th>
            <th scope="column3">Value</th>
            <th scope="column4">ROI</th>
            <th scope="column5">Current Market Price</th>
            <th scope="column6">Edit</th>
            <th scope="column7">Delete</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>AAPL</td>
            <td>BUY</td>
            <td>Undervalued</td>
            <td>10%</td>
            <td>$121.48</td>
            <td>
              <button className="btn">Edit</button>
            </td>
            <td>
              <button className="btn">Delete</button>
            </td>
          </tr>
        </tbody>
      </table>
    </div>
  )
}

export default StockHoldings;
