import React, { useState, useContext } from 'react';
import { useParams } from 'react-router-dom';
import { StocksContext } from "../../context/StocksContext";

const UpdateStock = (props) => {
  const { addStock } = useContext(StocksContext);
  const [ticker_symbol, setTicker_symbol] = useState("");
  const [recommendation_status, setRecommendation_status] = useState("Select");
  const [stock_value, setStock_value] = useState("Select");
  const { id } = useParams();
  
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(e);
  }

  return (
    <>
      <form action="submit" onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="ticker_symbol">Ticker Symbol</label>
          <input value={ticker_symbol} onChange={(e) => setTicker_symbol(e.target.value)} type="text" id="ticker_symbol" />
        </div>
        <div className="form-group">
          <label htmlFor="recommendation_status">BUY/HOLD/SELL</label>
          <input value={recommendation_status} onChange={(e) => setRecommendation_status(e.target.value)} type="text" id="recommendation_status" />
        </div>
        <div className="form-group">
          <label htmlFor="stock_value">VALUE</label>
          <select
            className="chooseStatus"
            value={stock_value} onChange={(e) => setStock_value(e.target.value)}
            >
            <option disabled>Select</option>
            <option value="BUY">BUY</option>
            <option value="HOLD">HOLD</option>
            <option value="SELL">SELL</option>
          </select>

        </div>
        <button className="btn">Submit</button>
      </form>
    </>
  )
}

export default UpdateStock;
