import React, { useEffect, useContext } from "react";
import { StocksContext } from "../../context/StocksContext";
import { useHistory } from "react-router-dom";
import "./StockHoldings.css";

const StockHoldings = (props) => {
  const { stocks, setStocks } = useContext(StocksContext);
  let history = useHistory();
  useEffect(() => {
    const fetchData = async () => {
      try {
        const url = "http://localhost:3000/api/v1/stocks";
        const options = {
          method: "GET",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
        };
        await fetch(url, options)
          .then((response) => {
            if (!response.ok) {
              throw new Error("Error: " + response.status);
            }
            return response.json();
          })
          .then((responseJson) => {
            setStocks(responseJson.holdings.stocks);
          });
      } catch (err) {
        console.log(err);
      }
    };
    fetchData();
  }, []);

  const handleDelete = async (id) => {
    try {
      const url = `http://localhost:3000/api/v1/stocks/${id}`;
      const options = {
        method: "DELETE",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
        },
      };
      await fetch(url, options).then((response) => {
        if (!response.ok) {
          throw new Error("Error: " + response.status);
        }
        // Find the specified id associated with the stock being deleted, and use filter to remove it and return the new array //
        setStocks(
          stocks.filter((stock) => {
            return stock.id !== id;
          })
        );
      });
    } catch (err) {
      console.log(err);
    }
  };

  const handleUpdate = (id) => {
    history.push(`/stocks/${id}/update`)
  };

  return (
    <div className="stockHoldings">
      <table className="holdingsTable">
        <thead>
          <tr className="tableRow">
            <th scope="column1">Ticker Symbol</th>
            <th scope="column2">Status</th>
            <th scope="column3">Value</th>
            <th scope="column4">ROI</th>
            <th scope="column5">Current Market Price</th>
            <th scope="column6">Edit</th>
            <th scope="column7">Delete</th>
          </tr>
        </thead>
        <tbody>
          {/* If stocks exist, run the code */}
          {stocks &&
            stocks.map((stock) => {
              return (
                <tr key={stock.id}>
                  <td>{stock.ticker_symbol}</td>
                  <td>{stock.recommendation_status}</td>
                  <td>{stock.stock_value}</td>
                  <td>ROI: %</td>
                  <td>Current Market Price</td>
                  <td>
                    <button onClick={() => handleUpdate(stock.id)} className="btn">Edit</button>
                  </td>
                  <td>
                    {/* Use arrow function as a reference to the function so it executes on delete */}
                    <button
                      onClick={() => handleDelete(stock.id)}
                      className="btn"
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              );
            })}
        </tbody>
      </table>
    </div>
  );
};

export default StockHoldings;
