import React, { useContext, useEffect } from "react";
import { useParams } from "react-router-dom";
import { StocksContext } from "../context/StocksContext";

const { id } = useParams();
const { selectedStock, setSelectedStock } = useContext(StocksContext);
const { selectedTickerSymbol, setSelectedTickerSymbol } = useContext(StocksContext); 

class StockDetails extends React.Component {
  render(props) {
    useEffect(() => {
      setSelectedTickerSymbol(selectedTickerSymbol);
      const fetchData = async () => {
        try {
          const url = `http://localhost:3000/api/v1/stocks/${id}`;
          const options = {
            method: "GET",
            headers: {
              Accept: "application/json",
              "Content-Type": "application/json",
            },
          };
          await fetch(url, options)
            .then((response) => {
              if (!response.ok) {
                throw new Error("Error: " + response.status);
              }
              return response.json();
            })
            .then((responseJson) => {
              setSelectedStock(responseJson.holdings.stock);
            });
        } catch (err) {
          console.log(err);
        }
      };
      fetchData();
      // Empty dependency array called here, which makes it run only once on MOUNT //
    }, []);
    const getData = (props) => {
      this.props.getStockPrice
    }
    return (
      <div>
        {/* Renders only if a stock was selected */}
        {selectedStock && selectedStock.ticker_symbol}
        <button onClick={this.props.getData}>
          Get Current Data
        </button>
      </div>
    );
  }
}

export default StockDetails;
