        <div className="formContainer">
          <Switch>
            <Route exact path = "/" component = {LandingPage} />
            <Route 
              exact path = "/home" 
              render={() => {
                return (
                  <Home
                    ROI={this.state.ROI}
                    currentMarketPrice={this.state.currentMarketPrice}
