import React, { useState } from "react";
import "./AddStockPost.css";

const AddStockPost = () => {
  const [ticker_symbol, setTicker_symbol] = useState("");
  const [recommendation_status, setRecommendation_status] = useState("");
  const [stock_value, setStock_value] = useState("Value Status");
  return (
    <div className="addStockSection">
      <form action="submit">
        <div className="form-row">
          <div className="form-column">
            <input
              type="text"
              className="inputTickerSymbol"
              placeholder="Ticker Symbol"
              value={ticker_symbol}
              onChange={(e) => setTicker_symbol(e.target.value)}
            />
          </div>
          <div className="form-column">
            <select
              placeholder="Choose"
              className="chooseStatus"
              value={recommendation_status}
              onChange={(e) => setRecommendation_status(e.target.value)}
            >
              <option disabled>Choose</option>
              <option value="1">BUY</option>
              <option value="2">HOLD</option>
              <option value="3">SELL</option>
            </select>
          </div>
          <div
            className="form-column"
            value={stock_value}
            onChange={(e) => setStock_value(e.target.value)}
          >
            <select className="chooseStockValue">
              <option disable>Value Status</option>
              <option value="1">Overvalued</option>
              <option value="2">Undervalued</option>
              <option value="3">Accurate</option>
            </select>
          </div>
        </div>
        <div className="commentSection">
          <textarea
            defaultValue="Enter a post here"
            name="comment"
            className="textbox"
          />
        </div>
        <div className="postButton">
          <button className="btn">Post!</button>
        </div>
      </form>
    </div>
  );
};

export default AddStockPost;
