import React, { useContext, useEffect } from "react";
import { useParams } from "react-router-dom";
import { StocksContext } from "../context/StocksContext";

const StockDetails = (props) => {
  const { id } = useParams();
  const { selectedStock, setSelectedStock } = useContext(StocksContext);
  const { selectedTickerSymbol, setSelectedTickerSymbol } = useContext(StocksContext); 

  useEffect(() => {
    const fetchData = async () => {
      try {
        const url = `http://localhost:3000/api/v1/stocks/${id}`;
        const options = {
          method: "GET",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
        };
        await fetch(url, options)
          .then((response) => {
            if (!response.ok) {
              throw new Error("Error: " + response.status);
            }
            return response.json();
          })
          .then((responseJson) => {
            setSelectedStock(responseJson.holdings.stock);
            // console.log(selectedStock.ticker_symbol);
            // this.setState = {
            //   selectedTickerSymbol: selectedStock.ticker_symbol
            // }
          });
      } catch (err) {
        console.log(err);
      }
    };
    fetchData();
    // Empty dependency array called here, which makes it run only once on MOUNT //
  }, []);

  const updateTicker = () => {
    return new Promise ((resolve => {
      props.setTicker(selectedStock.ticker_symbol);
      console.log(selectedStock.ticker_symbol);
    }))
    // .then((resolve =>{
    //   props.getStockPrice();
    // }))
  };

  const getData = () => {

  }
  
  return (
    <div>
      {/* Renders only if a stock was selected */}
      {selectedStock && selectedStock.ticker_symbol}
      {/* <button onClick={props.getStockPrice}>
        Get Current Data
      </button> */}
      {/* <button onClick={() => console.log(selectedStock.ticker_symbol)}>
        Ticker Symbol
      </button> */}
      <button onClick={updateTicker}>
        Update
      </button>
    </div>
  );
};

export default StockDetails;
